import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "../ExpensesFilter/ExpensesFilter";
import Card from "../UI/Card";
import "./Expenses.css";

const Expenses = (props) => {
  const [selected, setSelected] = useState("2021");

  const onChangeFilterHandler = (event) => {
    setSelected(event);
  };
  const expensesFilter = props.items.filter((expense) => {
    return expense.date.getFullYear().toString() === selected;
  });

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          value={selected}
          onChangeFilter={onChangeFilterHandler}
        />
        {expensesFilter.length === 0 && <p>No expenses found.</p> }
        {expensesFilter.length > 0 &&
          expensesFilter.map((expense) => (
            <ExpenseItem
              key={expense.id}
              title={expense.title}
              amount={expense.amount}
              date={expense.date}
            ></ExpenseItem>
          ))
        }
      </Card>
    </div>
  );
};

export default Expenses;
