import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import ExpensesFilter from "../ExpensesFilter/ExpensesFilter";
import Card from "../UI/Card";
import "./Expenses.css";

const Expenses = (props) => {
  const [selected, setSelected] = useState(new Date().getFullYear());
  const [expensesFilter, setExpensesFilter] = useState(props.items);

  const onChangeFilterHandler = (event) => {
    setSelected(event);
    setExpensesFilter((prevExpensesFilter) => {
       expensesFilter.filter(expense => {return expense.date.getFullYear() === selected}
      );
    });
  };

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          value={selected}
          onChangeFilter={onChangeFilterHandler}
        />
        {expensesFilter.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          ></ExpenseItem>
        ))}
      </Card>
    </div>
  );
};

export default Expenses;
