import { useContext, useEffect, useState } from "react";

import CartIcon from "../Cart/CartIcon";
import CartContext from "../store/cart-context";

import classes from "./HeaderCartButton.module.css";

const HeaderCartButton = (props) => {
  const [btnIsHightighted, setBtnIsHightighted] = useState(false);
  const cartCtx = useContext(CartContext);

  const nbrOfCartItems = cartCtx.items.reduce((curNumber, item) => {
    return curNumber + item.amount;
  }, 0);

  const btnClasses = `${classes.button} ${btnIsHightighted ? classes.bump : ''}`;

  useEffect(() => {
    setBtnIsHightighted(true);
  }, []);

  return (
    <button className={btnClasses} onClick={props.onClick}>
      <span className={classes.icon}>
        <CartIcon />
      </span>
      <span>Your Cart</span>
      <span className={classes.badge}>{nbrOfCartItems}</span>
    </button>
  );
};

export default HeaderCartButton;
